    public function getSourceContext()
    {
        return new Source("{% extends 'layout-admin.html.twig' %}
    {% block title %}Gestion Catégorie{% endblock %}

{% block style %}
<style>
main{
    margin: 20px 10px;
    padding-top:10px;
}
main ul li{
  color: #9D0806;
  font-size: 1rem;
}
main > div{
padding: 7px 10px;
}

main div{
padding-top: 7px;
}

main label{
  margin-right: 10px;
}
main input.btn{
margin-left:25px;
margin-top:20px;

}
  </style>
{% endblock %}
{% block h1 %}
\tTableau de bord
{% endblock h1 %}

{% block content %}
<main>
\t{{ form_start(form)}}
\t<div>
\t\t{{ form_label(form.nom)}}
\t\t{{ form_widget(form.nom)}}
        {{ form_errors(form.nom)}}

\t</div>
\t<div>

\t{{ form_row(form.image, {'attr' : {\"onChange\" : \"loadFile(event)\"}}
\t\t) }}
\t</div>


\t\t<div class=\"row justify-content-center\">
\t\t\t<img id=\"image\" {% if categorie.image %} src=\"{{ asset(\"upload/\" ~ categorie.image) }}\" 
            {% else %} src=\"{{ asset(\"images/default_image.jpg\") }}\" 
            {% endif %} style=\"width:300px\">
\t\t</div>


\t<input type=\"submit\" class=\"btn btn-success\" value=\"Enregistrer la modification\">

\t{{ form_end(form)}}
\t</main>
{% endblock %}


{% block javascripts %}
\t<script>
\t\tlet loadFile = function (event) {
let image = document.getElementById('image');
image.src = URL.createObjectURL(event.target.files[0]);
}
\t</script>
{% endblock %}
", "admin/categorie/editCategorie.html.twig", "C:\\code\\xamp\\htdocs\\Locavores\\templates\\admin\\categorie\\editCategorie.html.twig");
