        <div class=\"contact-detail my-3\">

            <div class=\"row\">
                <span class=\"my-auto col-4\">Nom d'utilisateur :</span>
                <div class=\"my-3 col-8\">
                        <p class=\"my-auto\">
                            {{ app.user.username }}
                        </p>
                </div>
            </div>

{% if is_granted('ROLE_SUPER_ADMIN') %}
            <div class=\"row\">
                <div class=\"card my-3 col border border-secondary\">
                    <div class=\"card-body\">
                        <p class=\"my-auto\">
