    {% for flashError in app.flashes('reset_password_error') %}
        <div class=\"alert alert-danger\" role=\"alert\">{{ flashError }}</div>
    {% endfor %}

    <h2 class=\"typo mb-5\">Réinitialisez votre mot de passe</h2>

      <h1>réinitialisez votre mot de passe</h1>

    {{ form_start(resetForm) }}
        {{ form_row(resetForm.plainPassword) }}
        <button class=\"btn btn1\">réinitialisez votre mot de passe</button>
    {{ form_end(resetForm) }}

    </div>
    {% endblock %}", "reset_password/reset.html.twig", "C:\\code\\xamp\\htdocs\\Locavores\\templates\\reset_password\\reset.html.twig");
